const express = require('express');
const cookieParser = require('cookie-parser');
const bodyParser = require('body-parser');
const app = express();

app.use(cookieParser())
app.use(bodyParser.json());
app.use(bodyParser.urlencoded({
  extended: true,
}));

app
.all('/', (req, res) => {
  console.log('Incomming Request \n');

  if(req.cookies){
    console.log('Request Cookies: ', req.cookies, '\n');
  }
  if(req.queries){
    console.log('Request Queries: ', req.queries, '\n');
  }
  if(req.body){
    console.log('Request Body: ', req.body, '\n');
  }
  if(req.params){
    console.log('Request Params: ', req.params, '\n');
  }

  res.json({
    'success': true
  })
});


app.listen(3030, () => {
  console.log("Server started at http://localhost:3030 \n");
})
